package JavaPatternmaking;

public class TranglePattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// declairing variable 
		int n =5;
		for(int i =1; i<=n; i++) {
			
//			 For printimg one star then 
			for(int j=1; j<=i; j++) {
				System.out.print("*");
			}
			System.out.println();
		}
	}

}
